package estruturasderepeticao;

public class TesteFor1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int x=0; x<3; x++) {
			System.out.println("x =" + x);
		}
		
		int y;
		for (y=3; y>=0; y--) {
			System.out.println("y = " + y);
		}
		System.out.println("Valor final de y = " + y);
	}

}
